import GenericEmail from "@workspace/transactional/emails/generic";
import { tool } from "ai";
import { Resend } from "resend";
import { MessageSchema } from "../types";
import { log } from "../utils/logger";

const DEFAULT_FOOTER =
  "This message is generated by an AI assistant. It may not reflect the views of the recipient. Please verify any information before acting upon it. If you want to learn more, visit [https://github.com/meleksomai/os](https://github.com/meleksomai/os).";

// Server-side tool that requires confirmation (no execute function)
export const sendEmailTool = (env: Env) =>
  tool({
    description:
      "Send an email reply with proper threading headers. Use this to respond to incoming emails.",
    inputSchema: MessageSchema,
    async execute(input) {
      const startTime = Date.now();
      try {
        const resend = new Resend(env.RESEND_API_KEY);

        const subject = input.subject.startsWith("Re:")
          ? input.subject
          : `Re: ${input.subject}`;

        const { data, error } = await resend.emails.send({
          from: input.from,
          to: input.to,
          subject,
          react: <GenericEmail content={input.raw} footer={DEFAULT_FOOTER} />,
          headers: {
            "In-Reply-To": input.messageId || "",
            References: input.references?.join(" ") || "",
          },
        });

        if (error) {
          log.error("[send-tool] failed", {
            error: error.message,
            to: input.to,
          });
          return { id: null, error: error.message };
        }

        if (!data) {
          log.error("[send-tool] failed", {
            error: "No data returned",
            to: input.to,
          });
          return { id: null, error: "No data returned from Resend" };
        }

        log.info("[send-tool] completed", {
          emailId: data.id,
          to: input.to,
          subject,
          durationMs: Date.now() - startTime,
        });
        return { id: data.id, error: null };
      } catch (err) {
        log.error("[send-tool] failed", {
          error: err instanceof Error ? err.message : String(err),
          to: input.to,
        });
        return {
          id: null,
          error: err instanceof Error ? err.message : String(err),
        };
      }
    },
  });
